#include<bits/stdc++.h>
#define rep(i,a,b) for(int i=a;i<=b;i++)
#define pre(i,a,b) for(int i=a;i>=b;i--)
#define N 100005
using namespace std;
int n,a[N],s[N],b[N],c[N];
int bt(int x){
	int sum=0;
	while(x)x>>=1,sum++;
	return sum;
}
int main(){
	scanf("%d",&n);
	rep(i,1,n)scanf("%d",&a[i]);
	rep(i,1,n)b[i]=bt(a[i]),c[b[i]]++;
	rep(i,1,32)if(c[i]>2){
		puts("1");return 0;
	}
	int ans=n;
	rep(i,1,n)s[i]=s[i-1]^a[i];
	rep(l,1,n-1)rep(r,l+1,n)rep(k,l,r-1){
		if((s[k]^s[l-1])>(s[r]^s[k]))ans=min(ans,r-l-1);
	}
	if(ans==n)puts("-1");else printf("%d\n",ans);
	return 0;
}
