}

bool chk(int i){
	if(vis[i]>2&&a[i])return 0;
	vis[i]++;
	if(!a[i]){
		if((a[i+1]+a[i+2])==2){
			s[++it]=i;
			a[i]=a[i+1]=a[i+2]=0;
		}
		//show();
		return 1;
	}
	if(i+2>n)return 0;
	if(a[i]){
		if((a[i+1]+a[i+2])==1){
			s[++it]=i;
			a[i]=a[i+1]=a[i+2]=0;
			//show();
			return 1;
		}
		if(!(a[i+1]+a[i+2])){
			s[++it]=i;
			a[i]=a[i+1]=a[i+2]=1;
			//show();
		}
		if(chk(i+2)){
			s[++it]=i;
			a[i]=a[i+1]=a[i+2]=0;
			//show();
			return 1;
		}
	}
	return 0;
}

