#include <iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>

using namespace std;

int n,dd;
int a[200];
int x[200],y[200];
int g[200][200];
int d[200];
int app[200];

int ab(int x)
{
	if (x < 0) return -x;else return x;
}
int q[300];

int main()
{
	cin >> n >> dd;
	memset(a,0,sizeof(a));
	for (int i = 2; i <= n-1; i++) scanf("%d", &a[i]);
	for (int i = 1; i <= n; i++) scanf("%d %d", &x[i], &y[i]);
	for (int i = 1; i <= n; i++)
		for (int j = 1; j <= n; j++)
		if (i == j) g[i][j] = 0; else g[i][j] = dd * (ab(x[i] - x[j]) + ab(y[i] -y[j])) - a[j];
	int t = 0;
	int w = 0;	
	q[t] = 1;
	for (int i = 1; i <= n;i++) d[i]= 500 * 100000;
	d[1] = 0;
	memset(app,0,sizeof(app));
	app[1] = 1;
	while (true)
	{
		int x = q[t];
		for (int i = 1; i <= n; i++)
		if (d[i] > d[x] + g[x][i])
		{
			d[i] = d[x] + g[x][i];
			if (app[i] == 0)
			{
				app[i] = 1;
				w = (w+1)% 300;
				q[w] = i;
			}
		} 
		app[q[t]] = 0;
		t = (t + 1)% 300;
		if (t == w) break;
	}	
	cout << d[n] << endl;
	//system("pause");
}
