#include <iostream>
#include <cstdio>
#include <cstring>
#include <algorithm>
#include <cmath>
#include <map>
#include <queue>
#include <vector>
#include <cstdlib>
#include <functional>
#include <string>
#define N 100010
using namespace std;
struct qph
{
    long long add,del;
    qph(){}
    qph(long long a,long long b)
    {
        add=a;del=b;
    }
};
struct node
{
    int v,next;
}e[N*2];
int head[N],cnt=0;
void add(int u,int v)
{
    e[cnt].v=v;
    e[cnt].next=head[u];
    head[u]=cnt++;
}
int a[N];
qph dfs(int u,int fa)
{
    long long add=0,del=0;
    for(int i=head[u];i!=-1;i=e[i].next)
    {
        int v=e[i].v;
        if(v==fa)continue;
        qph hh=dfs(v,u);
        long long x=hh.add,y=hh.del;
        add=max(add,x);del=max(del,y);
    }
    long long now=a[u]+add-del;
    if(now<0)add=add-now;
    if(now>0)del=del+now;
    qph ret=qph(add,del);
    return ret;
}

int main()
{
    int n;
    scanf("%d",&n);
    memset(head,-1,sizeof(head));
    cnt=0;
    for(int i=1;i<n;i++)
    {
        int u,v;
        scanf("%d%d",&u,&v);
        add(u,v);add(v,u);
    }
    for(int i=1;i<=n;i++)
     scanf("%d",&a[i]);
    qph ans=dfs(1,-1);
    cout<<ans.add+ans.del<<endl;
    return 0;
}
