#include <iostream>
using namespace std;


int c[505];

int mn[505][505];

int main(){
  int n;
  cin >> n;
  for (int i=0; i<n; ++i){
    cin >> c[i];
  }
  for (int l=1; l<=n; ++l){
    for (int a=0; a<n-l+1; ++a){
      int b=a+l-1;
      if (a==b) mn[a][b]=1;
      else if (a+1==b){
        if (c[a]==c[b]) mn[a][b]=1;
        else mn[a][b]=2;
      }else{
        if (c[a]==c[b])mn[a][b]=mn[a+1][b-1];
        else mn[a][b]=100000000;
        for (int c=a+1; c<=b; ++c){
          mn[a][b]=min(mn[a][b], mn[a][c-1]+mn[c][b]);
        }
      }
    }
  }
  cout << mn[0][n-1] << "\n";
}
