#include<algorithm>
#include<cstdio>
#include<vector>
const int maxn=1e5+5;
int t;
int n,m;
int a[maxn],b[maxn],mem[maxn];
bool can(int x){
	for(int i=1;i<=n-x;i++)if(a[i]>=b[i+x])return false;
	return true;
}
int go(int x){
	for(int i=2;i<=n;i++)a[i]=mem[i];
	a[1]=x,std::sort(a+1,a+n+1),std::sort(b+1,b+n+1);
	int ef_lef=0,ef_rig=n,ef_ans=-1;
	while(ef_lef<=ef_rig){
		int ef_mid=(ef_lef+ef_rig)>>1;
		if(can(ef_mid))ef_ans=ef_mid,ef_rig=ef_mid-1;
		else ef_lef=ef_mid+1;
	}
	return ef_ans;
}
signed main(){
	scanf("%d",&t);
	for(int wc=1;wc<=t;wc++){
		scanf("%d%d",&n,&m);
		for(int i=2;i<=n;i++)scanf("%d",a+i),mem[i]=a[i];
		for(int i=1;i<=n;i++)scanf("%d",b+i);
		int ef_lef=1,ef_rig=m,ef_ans=-1,nv=go(m);
		while(ef_lef<=ef_rig){
			int ef_mid=(ef_lef+ef_rig)>>1;
			if(go(ef_mid)==nv)ef_ans=ef_mid,ef_rig=ef_mid-1;
			else ef_lef=ef_mid+1;
		}
		printf("%lld\n",1ll*(nv-1)*(ef_ans-1)+1ll*nv*(m-ef_ans+1));
	}
	return 0;
}
