#pragma GCC optimize("Ofast,unroll-loops")
#pragma GCC target("avx,avx2,fma")
#include<bits/stdc++.h>
#define int long long
const int N=2e5+5,M=305,mod=1e9+7;
using namespace std;
int n,k,ans,s,siz[N],fac[N],inv[N];
basic_string<int>e[N];
int ksm(int a,int b){int res=1;for(;b;b>>=1,a=a*a%mod)if(b&1)res=res*a%mod;return res;}
int C(int m,int n){if(n>m)return 0;return fac[m]*inv[n]%mod*inv[m-n]%mod;}
void dfs(int x,int fa)
{
	siz[x]=1;
	for(auto y:e[x])
	if(y!=fa)
	{
		dfs(y,x);
		siz[x]+=siz[y];
		s=(s+C(siz[y],k/2)*C(n-siz[y],k/2)%mod)%mod;
	}
}
int x,y;
void solve()
{
	cin>>n>>k;
	if(k&1){cout<<1<<'\n';return;}
	for(int i=1;i<n;i++){cin>>x>>y;e[x]+=y;e[y]+=x;}
	ans=C(n,k);
	dfs(1,0);
	s=(s+ans)*ksm(ans,mod-2)%mod;
	cout<<s<<'\n';
}
int tt;
signed main()
{
	ios::sync_with_stdio(false);cin.tie(0);cout.tie(0);
	fac[0]=1;for(int i=1;i<N;i++)fac[i]=fac[i-1]*i%mod;
	inv[N-1]=ksm(fac[N-1],mod-2);
	for(int i=N-2;i>=0;i--)inv[i]=inv[i+1]*(i+1)%mod;
	tt=1;
//	cin>>tt;
	while(tt--)solve();
}
