#include <bits/stdc++.h>

using namespace std;

int n, m, t[5010][2], w[5010], a, b, x, z[5010];

int main()
{
	scanf("%d%d", &n, &m);
	for(int i=1; i<=n; ++i)
	{
		t[i][0]=123456789;
	}
	for(int i=0; i<m; ++i)
	{
		scanf("%d%d", &a, &b);
		++t[a][1];
		x=b-a;
		if(x<0)
		{
			x+=n;
		}
		t[a][0]=min(t[a][0], x);
	}
	for(int i=1; i<=n; ++i)
	{
		if(t[i][1])
		{
			w[i]=(t[i][1]-1)*n+t[i][0];
			z[i]=1;
		}
		w[i]+=i-1;
	}
	m=0;
	for(int i=1; i<=n; ++i)
	{
		if(z[i])
		m=max(m, w[i]);
	}
	cout<<m<<" ";
	for(int i=2; i<=n; ++i)
	{
		w[i-1]+=n-1;
		for(int j=1; j<=n; ++j)
		{
			if(j!=i-1)
			{
				--w[j];
			}
		}
		m=0;
		for(int i=1; i<=n; ++i)
		{
			if(z[i])
			m=max(m, w[i]);
		}
		cout<<m<<" ";
	}
	return 0;
}
