#include <bits/stdc++.h>
#define int long long
#define i64 long long
#define pii pair <int, int> 
using namespace std;
inline int read(void) {
    int x=0,sgn=1; char ch=getchar();
    while(ch<48||57<ch) {if(ch==45)sgn=0;ch=getchar();}
    while(47<ch&&ch<58) {x=x*10+ch-48;   ch=getchar();}
    return sgn? x:-x;
}
void write(int x) {
    if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10+'0');
}
#define lowbit(x) (x&(-x))
int n,sum,avg;
int a[200005];
int cnt[40],cnt2[40];
inline int f(int x) { return __builtin_ctzll(x); }
inline void work() {
    n=read(); sum=0;
    memset(cnt,0,sizeof cnt);
    memset(cnt2,0,sizeof cnt2);
    for(int i=1; i<=n; ++i) sum+=(a[i]=read());
    if(sum%n) return puts("No"),void();
    avg=sum/n;
    for(int i=1; i<=n; ++i) {
        int del=abs(a[i]-avg);
        if(del==0) continue;
        if(lowbit(del)==del) {
            if(a[i]<avg) ++cnt[f(del)+1],--cnt[f(del)];
            else         --cnt[f(del)];
            ++cnt2[f(del)];
            continue;
        }
        int cur=del+lowbit(del);
        if(lowbit(cur)!=cur) return puts("No"),void();
        if(a[i]<avg) cnt[f(cur)]++,cnt[f(lowbit(del))]--;
        else cnt[f(lowbit(del))]++,cnt[f(cur)]--;
    }
    for(int i=35; i>=1; --i) if(cnt[i]!=0) {
        if(cnt[i]<0||cnt[i]>cnt2[i-1]) return puts("No"),void();
        cnt[i-1]+=2*cnt[i];
        cnt[i]=0;
    }
    if(cnt[0]!=0) return puts("No"),void();
    else return puts("Yes"),void();
}
signed main() {
    int T=read();
    while(T--) work();
	return 0;
}
