#include<bits/stdc++.h>
#define ep 0.0000001
using namespace std;
struct Info{double x,y,r;}a[5];
struct Po{double x,y;}b[10];
int n,sum,su,p=2;
double di(int x,int y){
	return sqrt((a[x].x-a[y].x)*(a[x].x-a[y].x)+(a[x].y-a[y].y)*(a[x].y-a[y].y));
}
double d1(int x,int y){
	return sqrt((b[x].x-a[y].x)*(b[x].x-a[y].x)+(b[x].y-a[y].y)*(b[x].y-a[y].y));
}
Po pp(Info x,double y){
	Po p;
	p.x=x.x+x.r*cos(y);p.y=x.y+x.r*sin(y);
	return p;
}
double ab(double x){
	if (x>0) return x;
	return 0-x;
}
bool comp(const Po &a,const Po &b){if (ab(a.x-b.x)>ep) return a.x<b.x;return a.y<b.y;}
int main(){
	cin>>n;
	for (int i=1;i<=n;i++) cin>>a[i].x>>a[i].y>>a[i].r;
	for (int i=1;i<=n;i++)
		for (int j=1;j<=n;j++){
			if (i!=j&&a[i].r+a[j].r>=di(i,j)&&di(i,j)>=abs(a[i].r-a[j].r)) break;
			if (j==n) p++;
		}
	if (p==2+n) p--;
	for (int i=1;i<n;i++)
		for (int j=i+1;j<=n;j++)
			if (a[i].r+a[j].r>=di(i,j)&&di(i,j)>=abs(a[i].r-a[j].r)){
				double x=acos((a[i].r*a[i].r-a[j].r*a[j].r+di(i,j)*di(i,j))/(2*a[i].r*di(i,j)));
				double y=atan2(a[j].y-a[i].y,a[j].x-a[i].x);
				b[++sum]=pp(a[i],x+y);
				b[++sum]=pp(a[i],y-x);
			}
	sort(b+1,b+sum+1,comp);
	for (int i=1;i<sum;i++)
		if(ab(b[i].x-b[i+1].x)<=ep&&ab(b[i].y-b[i+1].y)<=ep){
			for (int j=i+1;j<=sum;j++) b[j-1]=b[j];
			sum--;i--;
		}
	for (int i=1;i<=sum;i++)
		for (int j=1;j<=n;j++)
			if (ab(d1(i,j)-a[j].r)<=ep) su++;
	cout<<su-sum+p<<endl;
	return 0;
}
