#include <bits/stdc++.h>

#define	st first
#define	nd second
#define	mp make_pair
#define	pb push_back
#define	lli long long int
#define	all( gg )	gg.begin(),gg.end()
#define	foreach( gg,itit )	for( typeof(gg.begin()) itit=gg.begin();itit!=gg.end();itit++ )
#define	FP( ii,aa,bb ) for( lli ii=aa;ii<=bb;ii++ )
#define	FM( ii,aa,bb ) for( lli ii=aa;ii>=bb;ii-- )
#define	debug(ccc)	cout << #ccc << " = " << ccc << endl;

#define	mod	1000000007LL

using namespace std;

lli	fact[30]={1},S;
int	n,n1,n2,arr[30];
int	k;

map<lli,int>	A[30],B[30];

void	rec1( int ind,int t,lli s ){
	if( s>S or t>k )	return;
	if( ind==n1+1 ){
		A[t][s]++;
		return;
	}
	rec1( ind+1,t,s );
	rec1( ind+1,t,s+arr[ind] );
	if( arr[ind]<=18 )	rec1( ind+1,t+1,s+fact[arr[ind]] );
}
void	rec2( int ind,int t,lli s ){
	if( s>S or t>k )	return;
	if( ind==n2+1 ){
		FP( i,t,k )
			B[i][s]++;
		return;
	}
	rec2( ind+1,t,s );
	rec2( ind+1,t,s+arr[ind] );
	if( arr[ind]<=18 )	rec2( ind+1,t+1,s+fact[arr[ind]] );
}

int main(){
	FP( i,1,19 )	fact[i] = fact[i-1]*i;
	cin >> n >> k >> S;
	FP( i,1,n )
		cin >> arr[i];
	sort( arr+1,arr+n+1 );
	n1 = (n+1)/2;
	n2 = n-n1;
	rec1( 1,0,0 );
	FP( i,1,n2 )
		arr[i] = arr[i+n1];
	rec2( 1,0,0 );
	lli	res=0;
	FP( i,0,k )
		foreach( A[i],it )
			if( B[k-i].find( S-(it->st) )!=B[k-i].end() )
				res += (lli)(it->nd)*B[k-i][S-(it->st)];
	cout << res << endl;
}
