#include <cstdio>
#include <cstring>
#include <algorithm>
#define eps 1e-8

using namespace std;

int gcd(int x,int y) {
  return (x%y==0)?y:gcd(y,x%y);
}

int main() {
  int n,k;
  scanf("%d%d",&n,&k);
  int t=k;
  for(int i=1;i<=n;i++) {
  	int x;
  	scanf("%d",&x);
  	x%=k;
  	if (x) t=gcd(t,x);
  }
  printf("%d\n",k/t);
  for(int i=0;i<k/t;i++) printf("%d ",i*t);
  printf("\n");
  return 0;
}
