#define N 55
struct Edge
{
	int u,v,w;
	Edge(int a=0,int b=0,int c=0) {u=a,v=b,w=c;}
};
vector<Edge> ans;
int id[N];
void get(int val) // 1~val
{
	val--;
	for(int i=2;i<=21;i++)
	{
		for(int j=1;j<=i-1;j++) ans.eb(i,j,1<<max(j-2,0));
	}
	for(int i=0;i<(int)ans.size();i++) ans[i].u=32-ans[i].u+1,ans[i].v=32-ans[i].v+1;
	for(int i=0;i<=19;i++) id[i]=32-i-1;
	int tmp=1;
	for(int i=0;i<=19;i++)
	{
		if(val>>i&1)
		{
			// printf("* %d %d %d\n",id[i],tmp);
			ans.eb(11,id[i],tmp),tmp+=1<<i;
		}
	}
	ans.eb(11,32,1);
}
signed main()
{
	int l=read(),r=read();
	get(r-l+1);
