#include<bits/stdc++.h>
using namespace std;

const int N=5e3+1e2+7;

int T;

int n,m;

long long f[121][121][2];

long long solve(long long t,int x,int y)
{
	if(t<(x+y))
		return 0;
	t=(t-(x+y)+1);
	memset(f,0,sizeof(f));
	f[0][0][0]=t;
	for(int i=0;i<120;i++)
		for(int j=0;j<120;j++)
		{
			long long s=f[i][j][0];
			f[i][j][0]=(s+1)/2;
			f[i][j][1]=s/2;
			f[i][j+1][0]+=(f[i][j][0]);
			f[i+1][j][0]+=(f[i][j][1]);
		}
	return f[x][y][0]+f[x][y][1];
}

int main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	cin>>T;
	while(T--)
	{
		long long t,x,y;
		cin>>t>>x>>y;
		long long a=solve(t,x,y);
		long long b=solve(t-1,x,y);
		if(x+y>t)
			cout<<"NO\n";
		else if(a!=b)
			cout<<"YES\n";
		else
			cout<<"NO\n";
	}
}
