using namespace std;

#define rep(i,n) for(int i = 0; i < (int)n; i++)
#define FOR(i,n,m) for(int i = (int)n; i <= (int)m; i++)
#define FOD(i,n,m) for(int i = (int)n; i >= (int)m; i--)

typedef long long i64;
typedef pair<int, int> PI;

#define sz(v) ((i64)(v).size())
#define all(v) (v).begin(),(v).end()
#define bit(n) (1LL<<(i64)(n))
#define each(i,v) for(__typeof((v).begin()) i = (v).begin(); i != (v).end(); i++)

#define PB push_back
#define MP make_pair
#define X first
#define Y second

template<class T> void fmax(T &a, T b) { if (a < b) a = b; }
template<class T> void fmin(T &a, T b) { if (a > b) a = b; }

const int N = 1e5 * 3 + 10;

int a[N][3], c[N], n, m, w[N];
queue<int> Q;

int main() {
	cin >> n >> m;
	FOR(i, 1, n) w[i] = -1;
	rep(i, m) {
		int x, y;
		cin >> x >> y;
		a[x][c[x]++] = y;
		a[y][c[y]++] = x;
	}
	FOR(z, 1, n) if (-1 == w[z]) {
		Q.push(z);
		int cnt = 0;
		while (!Q.empty()) {
			cnt++;
			if (cnt > n * 20) {
				cout << -1 << endl;
				return 0;
			}
			int i = Q.front();
			Q.pop();
			int t[2] = {0, 0};
			rep(j, c[i]) if (-1 != w[a[i][j]]) t[w[a[i][j]]]++;
			if (w[i] == (t[0] > t[1])) continue;
			w[i] = (t[0] > t[1]);
			rep(j, c[i]) Q.push(a[i][j]);
		}
	}
	FOR(i, 1, n) cout << w[i];
	cout << endl;
}
