#include<bits/stdc++.h>
using namespace std;

int main()
{
    int n;cin>>n;
    int l=0,r=1e9;
    char ss[15];
    map<int,bool> col;
    cout<<"0 1"<<endl;
    cin>>ss;n--;
    col[l]=(ss[0]=='b');
    while(n--)
    {
        int mid=(l+r)/2;
        cout<<mid<<" 1"<<endl;
        cin>>ss;
        col[mid]=(ss[0]=='b');
        if(col[mid]==col[l]) l=mid;
        else r=mid;
    }
    cout<<l<<" 2 "<<r<<" 0 "<<endl;
    return 0;
}
