#include<bits/stdc++.h>
#define N 100009
#define P 998244353
#define INF 0x3f3f3f3f3f3f3f3f
using namespace std;
typedef long long ll;
inline ll read() {
    ll x=0,f=1;int c=getchar();
    while(!isdigit(c)) {if(c=='-') f=-1;c=getchar();}
    while(isdigit(c)) {x=(x<<1)+(x<<3)+(c^48);c=getchar();}
    return x*f;
}
ll t,n,ans,lst,a[N],b1[N],b2[N],c[N],d[N];
void calc(ll x,ll y){
    for(int i=y+1;i<=x;i++) d[i]=(d[i-1]+c[i]*i%P)%P,ans+=c[i]*i%P,ans%=P;
}
void solve(){
    n=read();
    for(int i=1;i<=n;i++) b1[i]=b2[i]=c[i]=d[i]=0;
    for(int i=1;i<=n;i++) a[i]=read();
    ans=0;
    for(int i=1;i<=n;i++){
        lst=a[i];int j;
        b1[i]=b2[i]=a[i];
        for(j=i-1;j>=1;j--){
            if(b2[j]>lst){
                c[j]=(a[j]+lst-1)/lst;
                b1[j]=a[j]/c[j];
                if(a[j]%c[j]==0) b2[j]=b1[j];
                else b2[j]=b1[j]+1;
                lst=b1[j],c[j]--;
            }
            else{ans+=d[j],ans%=P;break;}
        }
        calc(i,j);
    }
}
int main(){
    //freopen(".in","r",stdin);
    //freopen(".out","w",stdout);
    t=read();
    while(t--) solve(),printf("%lld\n",ans);
    return 0;
}
