#define pll pair <long long , long long>
typedef long long ll;

const int N=(int)2e5+5;
int n,a[N],m,s[N],ans[N];


int main()
{
	ios:: sync_with_stdio(false);
	cin >> n;
	for(int i=0;i<n;i++)
		cin >> a[i];
	s[0]=a[0];
	for(int i=1;i<n;i++)
		s[i]=s[i-1]+a[i];
	ans[n-2]=s[n-1];
	m=s[n-2]-ans[n-2];
	for(int i=n-3;i>=0;i--)
	{
		if(i==0)ans[i]=max(m,s[n-1]); else ans[i]=max(m,s[n-1]-s[i-1]);
		m=max(m,s[i]-ans[i]);
	}
	cout << ans[0];
	return 0;
}
