#include<bits/stdc++.h>
using namespace std;
const int N=505;
char s[N][N];
int n,m,T;
int main(){
	cin>>T;
	while (T--){
		cin>>n>>m;
		for (int i=1;i<=n;i++)scanf("%s",s[i]+1);
		for (int i=1;i<=n;i++)
			if (i%3==1){
				for (int j=1;j<=m;j++)s[i][j]='X';
			}
			else if (i%3==0){
				int flag=0;
				for (int j=1;j<=m;j++)
					if (s[i][j]=='X'){
						s[i-1][j]='X';
						flag=1;
						break;
					}
				if (!flag){
					for (int j=1;j<=m;j++)
						if (s[i-1][j]=='X'){
							s[i][j]='X';
							flag=1;
							break;
						}
					if (!flag)s[i][1]=s[i-1][1]='X';
				}
			}
		if (n%3==0)for (int j=1;j<=m;j++)
			if (s[n][j]=='X')s[n-1][j]='X';
		for (int i=1;i<=n;i++)puts(s[i]+1);
		for (int i=1;i<=n;i++)
			for (int j=1;j<=m;j++)s[i][j]=0;
	}
}
