#include<bits/stdc++.h>
using namespace std;
map<pair<long long,long long>,int>res;
int main(){
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);
	cout.tie(NULL);

	long long N;
	cin>>N;
	long long lox=1,hix=N,loy=1,hiy=N;
	while(true){
		int ans;
		long long x=(lox+hix)/2;
		long long y=(loy+hiy)/2;
		if(!res[{x,y}]){
			cout<<x<<" "<<y<<endl;
			cin>>ans;
			res[{x,y}]=ans;
		}
		if(ans==0){
			return 0;
		}
		else if(ans==1){
			lox=x+1;
			if(lox>hix){
				hix=N;
			}
		}
		else if(ans==2){
			loy=y+1;
			if(loy>hiy){
				hiy=N;
			}
		}
		else{
			if(!res[{lox,y}]){
				cout<<lox<<" "<<y<<endl;
				cin>>ans;
				res[{lox,y}]=ans;
			}
			else{
				ans=res[{lox,y}];
			}
			if(ans==3||ans==1){
				hiy=y-1;
			}
			else if(ans==2){
				loy=y+1;
			}
			else if(ans==0){
				return 0;
			}
			if(!res[{x,loy}]){
				cout<<x<<" "<<loy<<endl;
				cin>>ans;
				res[{x,loy}]=ans;
			}
			else{
				ans=res[{x,loy}];
			}
			if(ans==3||ans==2){
				hix=x-1;
			}
			else if(ans==1){
				lox=x+1;
			}
			else if(ans==0){
				return 0;
			}
		}
	}
}
