#include<algorithm>
#include<cstdio>
#include<iostream>
#include<string>
#include<vector>

using namespace std;

typedef long long ll;

int main() {
  vector<int> h(6);
  for(int i = 0; i < 6; ++i)
    cin >> h[i];

  vector<ll> x(7);
  int maxi = (h[2]-h[0]+h[4]) / 2;
  bool found = 0;

  int best_len = 1e9;

  for(int t = 0; t <= maxi; ++t)
    {
      int x0 = h[2]-h[0]+h[4]-2*t;
      if(x0 % 2 || x0 < 0)
        continue;

      int x1 = h[5]-h[1]+h[2]-2*t;
      if(x1 % 2 || x1 < 0)
        continue;

      int x2 = h[5]-h[3]+h[4]-2*t;
      if(x2 % 2 || x2 < 0)
        continue;

      int x3 = h[0]-h[5]+h[1]-h[4] + 2*t;
      if(x3 % 2 || x3 < 0)
        continue;

      int x4 = -h[2] + h[0] - h[5] + h[3] + 2*t;
      if(x4 % 2 || x4 < 0)
        continue;

      int x5 = -h[2] + h[3] + h[1] - h[4] + 2*t;
      if(x5 % 2 || x5 < 0)
        continue;

      if((x0+x1+x2+x3+x4+x5)/2+t > best_len)
        continue;

      x[0] = x0 / 2;
      x[1] = x1 / 2;
      x[2] = x2 / 2;     
      x[3] = x3 / 2;
      x[4] = x4 / 2;     
      x[5] = x5 / 2;           
      x[6] = t;

      found = true;
      break;
    }

  if(!found)
    {
      cout << -1 << endl;
      return 0;
    }

  char s[7][5] = {"aabb", "abab", "abba", "abbb", "abaa", "aaba", "aaab"};
  vector<string> results(4);
  for(int i = 0; i < 7; ++i)
    for(int j = 0; j < x[i]; ++j)
      for(int k = 0; k < 4; ++k)
        results[k] += s[i][k];
  cout << results[0].size() << endl;
  for(int i = 0; i < 4; ++i)
    cout << results[i] << endl;
}
