#include<bits/stdc++.h>

using namespace std;

#define ll  long long
#define ld  double

#define sz(x)   (int)x.size()
#define all(x)  x.begin(),x.end()

#define pb  emplace_back
#define X   first
#define Y   second

const int   N   = 5e5 + 5;

typedef pair<int,int>   ii;
typedef vector<int>     vi;

int main()  {
    ios_base::sync_with_stdio(0);
    cin.tie(0); cout.tie(0);

    int itest;
    cin >> itest;

    while (itest--) {
        int n;  cin >> n;
        int m;  cin >> m;

        vector<vi>  g(n);
        vector<ll>  c(n);

        for(int i = 0 ; i < n ; ++i)    cin >> c[i];
        for(int i = 0 ; i < m ; ++i)    {
            int x;  cin >> x;
            int y;  cin >> y;

            g[y - 1].pb(x);
        }
        map<vi,ll>  mp;

        for(int i = 0 ; i < n ; ++i)    if (g[i].size())    {
            sort(all(g[i]));
            mp[g[i]] += c[i];
        }
        ll  ans = 0;

        for(auto it : mp)
            ans = __gcd(ans,it.second);

        cout << ans << "\n";
    }
}
