#include <bits/stdc++.h>
using namespace std;

using ll = long long;

void solve() {
    ll l, r;
    cin >> l >> r;
    l--;
    ll ans = 0;
    for (int i = 1, j; i <= l; i = j + 1) {
        j = l / (l / i);
        ll need = l / i + 2;
        ans += min<ll>(j, r / need);
        ans -= min<ll>(i - 1, r / need);
    }
    ans += max<ll>(0, r / 2 - l);
    cout << ans << "\n";
}

int main() {
    ios::sync_with_stdio(0), cin.tie(0);
    int T;
    cin >> T;
    while (T--) {
        solve();
    }
    return 0;
}
