#include "bits/stdc++.h"
using namespace std;
template<typename typC,typename typD> istream &operator>>(istream &cin,pair<typC,typD> &a) { return cin>>a.first>>a.second; }
template<typename typC> istream &operator>>(istream &cin,vector<typC> &a) { for (auto &x:a) cin>>x; return cin; }
template<typename typC,typename typD> ostream &operator<<(ostream &cout,const pair<typC,typD> &a) { return cout<<a.first<<' '<<a.second; }
template<typename typC,typename typD> ostream &operator<<(ostream &cout,const vector<pair<typC,typD>> &a) { for (auto &x:a) cout<<x<<'\n'; return cout; }
template<typename typC> ostream &operator<<(ostream &cout,const vector<typC> &a) { int n=a.size(); if (!n) return cout; cout<<a[0]; for (int i=1; i<n; i++) cout<<' '<<a[i]; return cout; }
template<typename typC> bool cmin(typC &x,const typC &y) { if (x>y) { x=y; return 1; } return 0; }
template<typename typC> bool cmax(typC &x,const typC &y) { if (x<y) { x=y; return 1; } return 0; }
#if !defined(ONLINE_JUDGE)&&defined(LOCAL)
#include "my_header\debug.h"
#else
#define dbg(...) ;
#define dbgn(...) ;
#endif
typedef unsigned int ui;
typedef long long ll;
#define all(x) (x).begin(),(x).end()
// void inc(ui &x,const ui &y) { if ((x+=y)>=p) x-=p; }
// void dec(ui &x,const ui &y) { if ((x+=p-y)>=p) x-=p; }
const int N=502,M=N*N;
int a[N][N],cnt[M],b[N][N],u[N*N],d[N*N],l[N*N],r[N*N];
int main()
{
	ios::sync_with_stdio(0); cin.tie(0);
	cout<<setiosflags(ios::fixed)<<setprecision(15);
	int n,m,q=0,i,j,k;
	cin>>n>>m;
	for (i=1; i<=n; i++) for (j=1; j<=n; j++) cin>>a[i][j],++cnt[a[i][j]];
	for (int x:cnt) q+=!!x;
	if (q<=m)
	{
		cout<<m-q<<endl;
		return 0;
	}
	m=q-m;
	memset(u,0x3f,sizeof u);
	memset(l,0x3f,sizeof l);
	for (i=1; i<=n; i++) for (j=1; j<=n; j++)
	{
		cmin(l[a[i][j]],j);
		cmax(r[a[i][j]],j);
		cmin(u[a[i][j]],i);
		cmax(d[a[i][j]],i);
	}
	for (k=1; k<=n; k++)
	{
		int L,R,U,D;
		memset(b,0,sizeof b);
		for (i=1; i<=n*n; i++) if (cnt[i])
		{
			L=max(1,r[i]-k+1);
			R=l[i];
			U=max(1,d[i]-k+1);
			D=u[i];
			if (L<=R&&U<=D)
			{
				// dbg(i,L,R,U,D);
				++R; ++D;
				++b[L][U];
				++b[R][D];
				--b[L][D];
				--b[R][U];
			}
		}
		for (i=1; i+k-1<=n; i++) for (j=1; j+k-1<=n; j++)
		{
			b[i][j]+=b[i-1][j]+b[i][j-1]-b[i-1][j-1];
			// dbg(k,i,j,b[i][j]);
			if (b[i][j]==m||b[i][j]-1==m)
			{
				cout<<1<<endl;
				return 0;
			}
		}
	}
	cout<<2<<endl;
}
