#include <bits/stdc++.h>
using namespace std;


const long long INF = (long long)1e18;

int N;
long long r1, r2, r3, d;

long long D[1010101];
long long c1[1010101], c2[1010101];
int main()
{
    ios::sync_with_stdio(false);
    cin >> N >> r1 >> r2 >> r3 >> d;
    for(int i=1; i<=N; ++i)
    {
        long long a; cin >> a;
        c1[i] = r1*a+r3+d; // costy
        c2[i] = min({r1*a+2*r1, r2+r1})+2*d; // not costy
    }
    D[N+1] = 0;
    for(int i=N; i>=1; --i)
    {
        D[i] = INF;
        if(i == N-1) // final 21 allow
            D[i] = min(D[i], c2[i]+c1[i+1]);

        // one pass
        D[i] = min(D[i], c1[i]+D[i+1]);

        // two pass using 2
        if(i != N)
            D[i] = min(D[i], c2[i]+c2[i+1]+D[i+2]);
    }
    // all 2 allow with extra point
    long long eq = 0; for(int i=1; i<=N; ++i) eq += c2[i]; eq += d;
    D[1] = min(D[1], eq);
    cout << D[1]-d << endl;
}
