#include<bits/stdc++.h>
#define rep(i,a,b) for(int i=a;i<=b;i++)
#define pre(i,a,b) for(int i=a;i>=b;i--)
#define N 500005
#define int long long
using namespace std;
int n,k,a[N],u[N],m;
priority_queue<int>q;
signed main(){
	scanf("%lld%lld",&n,&k);
	rep(i,1,n)scanf("%lld",&a[i]),a[i]=-a[i];
	sort(a+1,a+n+1);rep(i,1,n)a[i]=-a[i];
	int c=0,cur=1,ans=0;
	while(c>=0&&cur<=n)ans+=c,c+=a[cur++];
	rep(i,cur,n)u[++m]=a[i];
	sort(u+1,u+m+1);
	k++;rep(i,1,k)q.push(0);
	//rep(i,1,m)printf("%d ",u[i]);putchar('\n');
	rep(i,1,m){
		int x=-q.top();q.pop();
		ans+=x*u[i];q.push(-x-1);
	}
	printf("%lld\n",ans+c*(-q.top()));
	return 0;
}
