#include<bits/stdc++.h>

using namespace std;

int N = 1000000007;

long long powmod(long long a, long long b)
{
    if(b == 0)
        return 1;

    if(b == 1)
        return a;

    long long i = powmod(a,b/2);
    i*=i;
    i%=N;
    if(b%2 == 0)
        return i;
    else
        return((i*a)%N);
}

int main()
{
    long long a=2;
    long long b;
    int n;
    cin >> n;
    long long p=1;
    for(int i = 0; i < n;i++)
    {
        cin >> b;
        a = powmod(a,b);
        p*=(b%2);
    }
    a*= (N+1)/2;
    a%= N;
    b = a;
    if(p%2 == 0)
        b += 1;
    else
        b+= (N-1);
    b %= N;
    b *= (N+1)/3;
    b %= N;

    cout << b << "/" << a;
}
