#include <bits/stdc++.h>
using namespace std;

const int max_n = 2e5 + 5;

int a[max_n];
int last_occurence[max_n];

int main(){
	ios_base::sync_with_stdio(0);
	cin.tie(0);

	int n; cin >> n;
	for (int i = 1; i <= n; i++){
		cin >> a[i];
	}

	for (int i = 1; i <= n; i++){
		last_occurence[a[i]] = i;
	}
	int latest_occurence = 0, covered = 0, answer = 0;
	for (int i = 1; i <= n; i++){
		latest_occurence = max(latest_occurence, last_occurence[a[i]]);
		if (covered > i) answer++;
		else covered = latest_occurence;
	}

	cout << answer << "\n";
}
