#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <algorithm>
#include <vector>
#include <set>
#include <map>
#include <cstring>
#include <string>
#include <ctime>

using namespace std;

#define file "e"
#define sqr(a) ((a)*(a))
#define two(a) (1 << (a))

int N, a[100005], b[100005], good[100005];

long long s[200005];

set <pair <long long, int> > st;

void Load ()
{
    scanf ("%d", &N);

    for (int i = 0;i < N;i++)
        scanf ("%d", &a[i]);
    for (int i = 0;i < N;i++)
        scanf ("%d", &b[i]);
}

void go (bool rev)
{
    st.clear();
    s[0] = a[0] - b[0];
    for (int i = 1;i < 2 * N;i++)
        s[i] = s[i - 1] + a[i%N] - b[i%N];

    for (int i = 0;i < N;i++)
        st.insert (make_pair (s[i], i));

    for (int i = 0;i < N;i++)
    {
        int cur = (*st.begin()).second;
        if (s[cur] - (i > 0 ? s[i - 1] : 0) >= 0)
            good[rev ? N - (i % N) - 1 : (i % N)] = 1;

        st.erase (make_pair (s[i], i));
        st.insert (make_pair (s[i + N], i + N));
    }
}

void Solve ()
{
    go (0);
    reverse (a, a + N);
    reverse (b, b + N);
    int t = b[0];
    for (int i = 0;i < N - 1;i++)
        b[i] = b[i + 1];
    b[N - 1] = t;
    go (1);

    int ans = 0;
    for (int i = 0;i < N;i++)
        if (good[i]) ans++;
    cout << ans << endl;

    for (int i = 0;i < N;i++)
        if (good[i]) printf ("%d ", i + 1);
}

int main ()
{
    //freopen (file".in", "r", stdin);
    //freopen (file".out", "w", stdout);

    Load();
    Solve();

    return 0;
}
