#include<cstdio>
#include<iostream>
#include<cstdlib>
#include<cstring>
#include<algorithm>
#include<cmath>
using namespace std;

double a[100010][101];
long long c[1000010][6];
int s[100010];
double b[101];

void doit(int u,int v,int k)
{
    memset(b,0,sizeof(b));
    for (int i=0;i<=100;i++)
        for (int j=0;j<=min(i,k);j++)
            b[i-j]+=a[u][i]*c[i][j]*c[s[u]-i][k-j]/c[s[u]][k];
    //k=j; N=s[u]; m=i; n=k;
    for (int i=0;i<=100;i++)
        a[u][i]=b[i];
    s[u]-=k;
    s[v]+=k;
}

int main()
{
    memset(c,0,sizeof(c));
    c[0][0]=1;
    for (int i=1;i<=1000000;i++)
        for (int j=0;j<=min(5,i);j++)
            if (j==0 || j==i) c[i][j]=1;
            else c[i][j]=c[i-1][j-1]+c[i-1][j];

    int n;
    scanf("%d",&n);
    for (int i=0;i<n;i++)
        scanf("%d",&s[i]);
    memset(a,0,sizeof(a));
    for (int i=0;i<n;i++)
        a[i][s[i]]=1;
    int q;
    scanf("%d",&q);
    double ans=0;
    for (int i=0;i<n;i++)
        ans+=a[i][0];
    for (int i=0;i<q;i++)
    {
        int u,v,k;
        scanf("%d%d%d",&u,&v,&k);
        u--,v--;
        double tmp=a[u][0];
        doit(u,v,k);
        ans+=a[u][0]-tmp;
        printf("%.12lf\n",ans);
        /*
        for (int i=0;i<n;i++)
            for (int j=0;j<=2;j++)
                printf("%d %d: %.12lf\n",i,j,a[i][j]);
        printf("\n");
        */
    }

    //system("pause");
    return 0;
}
