#include<bits/stdc++.h>
#define N 200100
using namespace std;
const int MOD=1e9+7;
typedef pair<int,int> pii;
map<pii,int> s;
pii nu[N];
int n,len,v,tmp=1;
int a[N],b[N];
int vis[N],beg[N];
int to[N<<1],nex[N<<1];
int f;
inline void Add(int a,int b){
	nex[++len]=beg[a],beg[a]=len,to[len]=b;
	nex[++len]=beg[b],beg[b]=len,to[len]=a;
}
int dfs(int p,int fa){
	vis[p]=1;
	int ans=1;
	for(int i=beg[p];i;i=nex[i]){
		if(to[i]==fa) continue;
		if(vis[to[i]]){
			f=0;
			continue;
		}
		ans+=dfs(to[i],p);
	}
	return ans;
}
int main(){
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%d%d",&a[i],&b[i]);
		nu[len++]=pii(a[i],0);
		nu[len++]=pii(b[i],1);
	}
	sort(nu,nu+len);
	int tmp=1;
	s[nu[0]]=1;
	for(int i=1;i<len;i++){
		if(nu[i]!=nu[i-1]) tmp++;
		s[nu[i]]=tmp;
	}
	for(int i=0;i<n;i++){
		int l=s[pii(a[i],0)],r=s[pii(b[i],1)];
		if(l!=r) Add(l,r);
	}
	long long ans=1;
	for(int i=1;i<=tmp;i++){
		if(vis[i]) continue;
		f=1;
		int sz=dfs(i,0),bas=1;
		while(sz--) bas=bas*2%MOD;
		bas=(bas-f+MOD)%MOD;
		ans=ans*bas%MOD;
	}
	printf("%I64d",ans);
	return 0;
}
