#include <bits/stdc++.h>
using namespace std;
#define LL long long
int pow_mod(int x, int n, int mod){
    if(n==0) return 1;
    int ret=pow_mod((LL)x*x%mod,n/2,mod);
    if(n&1) ret=(LL)ret*x%mod;
    return ret;
}
int main(){
    int n;
    scanf("%d",&n);
    if(n==1){
        printf("YES\n1\n");
    }else if(n==4){
        printf("YES\n");
        printf("1\n3\n2\n4\n");
    }else{
        for(int i=2; i<=n/2; ++i){
            if(n%i==0){
                printf("NO\n");
                return 0;
            }
        }
        printf("YES\n");
        puts("1");
        for(int i=2; i<n; ++i){
            printf("%d\n",(LL)i*pow_mod(i-1,n-2,n)%n);
        }
        printf("%d\n",n);
    }

    return 0;
}
