#include<iostream>
#include<algorithm>
#include<cstdio>
#include<cmath>
#include<cstring>
#include<queue>
#include<vector>
#include<cstdlib>
#include<set>
using namespace std;
#define N 510000
char str[N];
long long s1[N],s2[N],s3[N];
double ans=0;
int n;
bool pd(char ch){
     return ch=='I' || ch=='E' || ch=='A' || ch=='O' || ch=='U' || ch=='Y';
     }
int main(){
    scanf("%s",str);
    n=strlen(str);
    for(int i=1;i<=n;i++)
      if(pd(str[i-1])){
        int t1=min(i,n-i+1),t2=max(i,n-i+1);
        s1[1]++;
        s1[t1]--;
        s2[t1]+=t1;
        s2[t2+1]-=t1;
        s3[n]++;
        s3[t2]--;          
      }
    for(int i=1;i<=n;i++)s1[i]+=s1[i-1],s2[i]+=s2[i-1];
    for(int i=n;i;i--)s3[i]+=s3[i+1];
    for(int i=1;i<=n;i++){
      long long s=s1[i]*i+s2[i]+s3[i]*(n-i+1);
      ans+=(double)s/i;
    }
    printf("%.7lf\n",ans);
    return 0;
}
