#include <bits/stdc++.h>
using namespace std;
#define fo(i,a,b) for(int i=(a);i<(b);i++)
#define MOD 1000000007
#define MP make_pair
#define PB push_back
typedef long long ll;

int N;
ll dp[10100100];
ll K, a[1100100], lo, hi, mid, ans=-1;

bool check (ll v) {
	fo(i, 0, 10100100) dp[i] = 0;
	fo(i, 0, N) dp[a[i]]++;
	ll sum = 0;
	for (int i = 10100100; i--; ) {
		if (i/2 < v) {
			if (i >= v) {
				sum = min(K, sum + dp[i]);
			}
		} else {
			dp[i/2] = min(K, dp[i/2] + dp[i]);
			dp[(i+1)/2] = min(K, dp[(i+1)/2] + dp[i]);
			dp[i] = 0;
		}
	}
	return sum >= K;
}

int main () {
	scanf("%d %lld", &N, &K);
	fo(i, 0, N) scanf("%lld", &a[i]);
	sort(a, a+N);

	lo = 1, hi = a[N-1];
	while (lo <= hi) {
		mid = (lo + hi) / 2;
		if (check(mid)) ans = mid, lo = mid+1;
		else hi = mid-1;
	}

	printf("%lld\n", ans);

	return 0;
}
