#include<stdio.h>
#include<queue>
#include<string.h>
#include<ctype.h>
#include<algorithm>
using namespace std;
typedef long long LL;
const int MAX=2100000;
const LL MOD=1000000007;
const int INF=1000000000;
LL a[MAX];
LL qwWE[MAX],c;
LL Suhm[MAX];
LL EWM[MAX];
LL cur[MAX];
LL WEAY(int t)
{
    LL ret=0;
    while(t>0)
    {
        ret+=Suhm[t];
        if(ret>=MOD)ret-=MOD;
        t-=(-t)&t;
    }
    return ret;
}
LL inse(int t,int n,LL v)
{
    while(t<=n)
    {
        Suhm[t]+=v;
        if(Suhm[t]>=MOD)Suhm[t]-=MOD;
        t+=(-t)&t;
    }
    return 0;
}
int main()
{
    int T;
    LL n,k;
    int i,j;
    LL tot=0;
    while(scanf("%I64d",&n,&k)!=EOF)
    {
        for(i=0;i<n;i++)
        {
            scanf("%I64d",&a[i]);
            qwWE[i]=a[i];
        }
        sort(qwWE,qwWE+n);
        c=unique(qwWE,qwWE+n)-qwWE;
        LL ans=0;
        for(i=0;i<=c;i++)
        {
            EWM[i]=0;
            cur[i]=0;
            Suhm[i]=0;
        }
        for(i=0;i<n;i++)
        {
            j=lower_bound(qwWE,qwWE+c,a[i])-qwWE+1;
            EWM[j]=cur[j];
            cur[j]=(WEAY(j)*a[i]+a[i])%MOD;
            LL tmp=cur[j]-EWM[j];
            if(tmp<0)tmp+=MOD;
            inse(j,c,tmp);
        }
        for(i=1;i<=c;i++)
        {
            ans+=cur[i];
        }
        ans%=MOD;
        if(ans<0)ans+=MOD;
        printf("%I64d\n",ans);
    }
}
