#include "bits/stdc++.h"

using namespace std;

using ll = long long;

const int md = 998244353;

void solve() {
    int n;
    cin >> n;
    vector<int> s(n);
    for (auto &x: s) cin >> x;
    vector<int> pref(s[n - 1] + 1);
    for (auto &x: s) pref[x]++;
    for (int i = 1; i <= s[n - 1]; ++i) pref[i] += pref[i - 1];
    ll ans = 0;
    array<int, 3> current = {-1, -1, 0};
    for (int x = 1; x <= s[n - 1]; ++x) {
        int y = s[n - 1] / x;
        int c = min(1, s[n - 1] % x);
        if (current[0] != y || current[1] != c) {
            int result = 0;
            for (int l = y, r = y + c; l <= s[n - 1]; l += y, r += y + c) {
                r = min(r, min(s[n - 1], l + y - 1));
                result += pref[r] - pref[l - 1];
            }
            current = {y, c, result};
        }
        ans += 1ll * current[2] * x;
        ans %= md;
    }
    cout << ans << '\n';
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int t = 1;
    cin >> t;

    while (t--) {
        solve();
    }

    return 0;
}
