#include <bits/stdc++.h>
using namespace std;
typedef long long LL;
const int MX = 2500005;
const int M = 1000000007;
const int mod = M;
LL inv[MX], f[MX], g[MX];
void init() {
  int i;
  inv[1] = 1;
  for (i = 2; i < MX; i++) inv[i] = inv[M % i] * (M - M / i) % M;
  f[0] = g[0] = 1;
  for (i = 1; i < MX; i++) {
    f[i] = f[i - 1] * i % M;
    g[i] = g[i - 1] * inv[i] % M;
  }
}
LL ext_gcd(LL a, LL b, LL& x, LL& y) {
  LL g;
  if (b) {
    g = ext_gcd(b, a % b, y, x);
    y -= a / b * x;
  }
  else {
    g = a;
    x = 1, y = 0;
  }
  return g;
}
LL C[MX];
LL A[MX], B[MX];
int main() {
	#ifndef ONLINE_JUDGE
	freopen("in.txt", "r", stdin);
	#endif
	ios::sync_with_stdio(0);
	cin.tie(0);
	LL d;
	cin >> d;
	init();
	for (int i = 0; i <= d; i ++) cin >> A[i];
	for (int i = 0; i <= d; i ++) cin >> B[i];
	for (int i = 0; i <= d; i ++) {
		C[i] = (g[d - i] * g[i]) % M;
		if (i % 2) C[i] = (M - C[i]) % M;
	}
	LL t1, t2, t3;
	t1 = t2 = 0;
	for (int i = 0; i <= d; i ++) {
		t1 = (t1 + (A[i] * ((C[i] * d) % mod)) % mod) % mod;
		t3 = (((d + 1) * d / 2) % mod + mod - i) % mod;
//		cerr << t3 << endl;
		t2 = (t2 + (((B[i] - A[i] + mod) * t3 % mod) * C[i] % mod)) % mod;
	}
	LL x1, x2;
	ext_gcd(t1, mod, x1, x2);
	x1 = (x1 % mod + mod) % mod;
	cout << (mod - (t2 * x1) % mod) % mod << endl;
	return 0;
}
