#include <bits/stdc++.h>
using namespace std;
mt19937 rng(chrono::steady_clock::now().time_since_epoch().count());

inline bool good(int& l1, int& r1, int& l2, int& r2){
	if (l1 > l2){
		swap(l1, l2);
		swap(r1, r2);
	}

	//l1 <= l2
	if (r1 < l2) return true;
	int r1_new = l2 - 1;
	int l2_new = r1 + 1;
	r1 = r1_new;
	l2 = l2_new;
	return l1 <= r1 && l2 <= r2;
}

vector<int> solve(int k, int sz, const vector<long long>& g){
	map<long long, pair<int, int>> memo;
	vector<long long> s(sz);
	s[0] = g[0];
	for(int i = 1; i < sz; ++i){
		s[i] = s[i - 1] ^ g[i];
	}

	int it = 0;
	while(true){
		it++;
		int r = rng() % sz;
		int l = rng() % (r + 1);
		long long value = s[r] ^ (l == 0 ? 0 : s[l - 1]);
		if (memo.count(value)){
			int l2 = memo[value].first;
			int r2 = memo[value].second;
			int l1 = l, r1 = r;
			if (good(l1, r1, l2, r2)){
				return {l1, r1, l2, r2};
			}
		}

		memo[value] = {l, r};
	}
}

int main(){
	int t;
	cin >> t;
	while(t--){
		int k; 
		cin >> k;
		int sz = 1 << (k + 1);
		vector<long long> g(sz);
		for(int i = 0; i < sz; ++i){
			cin >> g[i];
		}

		vector<int> result = solve(k, sz, g);
		for(int i: result){
			cout << i + 1 << ' ';
		}
		cout << endl;
	}

	return 0;
}
