#include <iostream>
#include <cstdio>
#include <string.h>
#include <string>
#include <algorithm>
#include <cmath>
#include <vector>

using namespace std;
long long a[1111111], f[2468647],n,k;

int main()
{
	cin >> n >> k;
	for(int i=0; i<n; i++) cin >> a[i];
	sort(a,a+n);
	for(int i=0; i<=n; i++)
	{
		for(int j=a[i]+1; j<=a[i+1]; j++) f[j]=a[i];
	}
	for(int i=a[n-1]+1; i<=1111*1111*2; i++) f[i]=a[n-1];
	long long ans=0;
	long long mi=min(k+1,a[0]);
	for(int i=mi; i<=a[0]; i++)
	{
		long long ma=0;
		for(int j=i; j<=1111*1111*2; j+=i) if (f[j]>=j-i) ma=max(ma, i+f[j]-j);
		if (ma<=k) ans=i;
	}
	cout << ans;
	cin >> n;
	return 0;
}
