#include <cstdio>
#include <map>
typedef long long LL;
typedef std::pair<LL, LL> node;
const int mod=1000000007;

LL n, a, b;
inline LL getint();
inline void putint(int);

std::map<node, int> s;
int solve(LL, LL);

int main()
{
	n=getint(), a=getint(), b=getint();
	if (n==1 && a==1)
	{
		if (b!=1) putint(0);
		else putchar('i'), putchar('n'), putchar('f'), putchar('\n');
	}
	else if (n==a && a==b)
		putint(2);
	else
		putint(solve(a, b));

	return 0;
}
inline LL getint()
{
	register LL num=0;
	register char ch;
	do ch=getchar(); while (ch<'0' || ch>'9');
	do num=num*10+ch-'0', ch=getchar(); while (ch>='0' && ch<='9');
	return num;
}
inline void putint(int num)
{
	char stack[11];
	register int top=0;
	if (num==0) stack[top=1]='0';
	for ( ;num;num/=10) stack[++top]=num%10+'0';
	for ( ;top;top--) putchar(stack[top]);
	putchar('\n');
}
int solve(LL x, LL y)
{
	if (!x && !y) return 1;
	if (!x || !y) return 0;
	if (s.count(node(x, y))) return s[node(x, y)];
	int & t=s[node(x, y)];
	t=0;
	for (LL i=y%a;i<=x;i+=a) if ((x-i)%n==0)
		t=(t+solve((x-i)/n, (y-i)/a))%mod;
	return t;
}
