#include<bits/stdc++.h>
using namespace std;
int n,q,a[300003];
int f[300003],ans[300003],pre[300003];
int main(){
	ios::sync_with_stdio(0);
	cin.tie(NULL);
	cout.tie(NULL);
	cin>>n;
	for(int i=1;i<=n;i++)
		cin>>a[i];
	sort(a+1,a+n+1);
	for(int i=1;i<=n;i++){
		if(a[i]<=i)
			f[i]=max(f[i],pre[i-a[i]]+1);
		pre[i]=max(pre[i-1],f[i]);
	}
	for(int i=1;i<=n;i++)
		if(f[i])
			ans[f[i]+n-i]=i;
		else
			ans[n-a[i]+1]=i;
	for(int i=n;i>0;i--)
		ans[i]=max(ans[i],ans[i+1]);
	cin>>q;
	while(q--){
		int x;
		cin>>x;
		cout<<ans[x]<<'\n';
	}
}
