#include<bits/stdc++.h>
using namespace std;

#define endl '\n'
#define int long long

const int N=2e3+5;

void solve() {
    int n;
    cin>>n;
    vector<tuple<int,int,int>>ans;
    int now=n,low=n/2+2;
    while(now!=2){
        if(now<=low){
            ans.emplace_back(1,now-1,now);
            break;
        }
        if(now%4==0)ans.emplace_back(now/2-1,now-1,now);
        else if(now%2==0)ans.emplace_back(now/2,now-2,now-1);
        else ans.emplace_back(now-2,now-1,now);
        now-=3;
    }
    cout<<ans.size()<<endl;
    for(auto [i,j,k]:ans)cout<<i<<' '<<j<<' '<<k<<endl;
}

signed main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);

    int T=1;
    cin>>T;
    while(T--){
        solve();
    }
}
