#define INF 2147483647
#define LINF 1000000000000000000LL
#define EPS 1e-9
#define debug(x) cerr << #x << " = " << x << endl
#define FOR(x,y) for(int x=0;x<y;x++)
#define FORU(x,y) for(int x=1;x<=y;x++)
using namespace std;


typedef long long ll;
typedef pair<int,int> PII;
typedef vector<int> VI;
typedef vector<VI> VVI;
typedef vector<bool> VB;
typedef vector<VB> VVB;
typedef vector<string> VS;
typedef map<int,int> MII;
typedef MII::iterator iMII;

int minzer=0,minu=0,maxzer=0,maxu=0;
string s;

inline bool equilibri(){
    return (maxzer>= int(s.size()/2) && minzer<=int(s.size()/2)
        &&  maxu>=int(s.size()/2)+s.size()%2 && minu<=int(s.size()/2)+s.size()%2);
}

int main(){
//ios_base::sync_with_stdio(false);
    while(cin>>s){
        minzer=minu=maxzer=maxu=0;
        int bcu=-1,wcu=-1,bcz=-1,wcz=-1;
        FOR(i,s.size()) {
            if(s[i]=='0') {
                wcz=i;
                bcz=i;
                minzer++;
            }
            if(s[i]=='1') {
                wcu=i;
                bcu=i;
                minu++;
            }
            if(s[i]=='?') {
                bcu=bcz=i;
                maxzer++;
            }
        }
        maxu=maxzer+minu;
        maxzer+=minzer;
        //00 (Favoritza el maxzer)
        if(maxzer>=int((s.size()-2)/2)+2) cout<<"00"<<endl;
        if(equilibri()){
            //01
            if(wcu>bcz) cout<<"01"<<endl;
            else{
                if(int(s.size()/2)+s.size()%2-minu>0 && bcu>wcz) cout<<"01"<<endl;
            }
            //10
            if(wcz>bcu) cout<<"10"<<endl;
            else{
                if(int(s.size()/2)-minzer>0 && bcz>wcu) cout<<"10"<<endl;
            }
        }
        if(maxu>=min(s.size()-2,int((s.size()-2)/2)+s.size()%2)+2) cout<<"11"<<endl;
    }
}
