#include<bits/stdc++.h>
using namespace std;
#define mod 998244353
#define IOS ios::sync_with_stdio(0)
#define N 1000005
#define ls k<<1
#define rs k<<1|1
#define mid (L+R>>1)
#define low(x) (x&-x)
#define ll long long
int T,n,l,ys[N],r,i,j,k,a[N],pos[N],gg,tot; 
ll ans[N];
ll c[N];
struct query{
	int l,r,pos;
}tmp,q[N];
query mk(int l,int r,int pos){
	tmp.l=l;
	tmp.r=r;
	tmp.pos=pos;
	return tmp;
}
inline bool cmp(query aa,query bb){
	return aa.l<bb.l;
}
void add(int k,int y){
	while(k<N){
		c[k]+=y;
		k+=low(k);
	}
}
ll sum(int k){
	ll ret=0;
	while(k){
		ret+=c[k];
		k-=low(k);
	}
	return ret;
}
ll f3(ll x){
	return x*(x-1)*(x-2)/6;
}
signed main(){
	IOS;
	cin>>T;
	for(i=1;i<=T;++i){
		cin>>l>>r;
		q[++tot]=mk(l,r,i);
		int f1=(l*2+5)/6*6;
		ans[i]=max(0,r-f1+6)/6;
		f1=(l*5+1)/2;
		f1=(f1+14)/15*15;
		ans[i]+=max(0,r-f1+15)/15;
		ans[i]=f3(r-l+1)-ans[i];
	}
	sort(q+1,q+tot+1,cmp);
	for(i=1;i<=200000;++i){
		for(j=i;j<=200000;j+=i){
			++ys[j];
		}
	}
	for(i=1;i<=200000;++i)add(i,(ys[i]-2)*(ys[i]-1)/2);
	q[0].l=1;
	for(i=1;i<=T;++i){
		for(j=q[i-1].l;j<q[i].l;++j){
			for(k=j;k<=200000;k+=j){
				add(k,2-ys[k]);
				--ys[k];
			}
		}
		ans[q[i].pos]-=sum(q[i].r)-sum(q[i].l+1);
	}
	for(i=1;i<=T;++i){
		cout<<ans[i]<<"\n";
	}
}
