#include <bits/stdc++.h>
#define ld long double
#define ll long long int
#define st first
#define nd second
#define pb push_back
#define pii pair<int,int>
#define mp make_pair

using namespace std;

const int N = 1e6 + 5;
vector<int> adj[N];
int sz[N];
ll ans = 0;
int n;
bitset<N> dp;

priority_queue<int> Q;
vector<int> its;
stack<int> now;

const int TR2 = 6666;
const int TR3 = 155;

bitset<TR2> dp2;
int tot;

ll brute(){
    dp2.reset();
    dp2[0] = true;
    for(int x : its){
        dp2 |= (dp2 << x);
    }
    ll mx = 0;
    for(int i=0;i<=tot-1;i++){
        if(dp2[i]) mx = max(mx, 1LL * i * (tot - 1 - i));
    }
    return mx;
}

bitset<TR3> dp3;

ll brute2(){
    dp3.reset();
    dp3[0] = true;
    for(int x : its){
        dp3 |= (dp3 << x);
    }
    ll mx = 0;
    for(int i=0;i<=tot-1;i++){
        if(dp3[i]) mx = max(mx, 1LL * i * (tot - 1 - i));
    }
    return mx;

}

void clr(){
    while(!Q.empty()) Q.pop();
    while(!now.empty()) now.pop();
    its.clear();
}

vector<int> childs[N];


void ev(int v){
    int big = 0;
    sz[v] = 1;
    for(int x : childs[v]){


            sz[v] += sz[x];
            big = max(big, sz[x]);

    }

    if(big >= (sz[v] - 1) / 2){
        ll one = big;
        ll two = sz[v] - 1 - one;
        ans += one * two;
        return;
    }

    clr();
    for(int x : childs[v]){
        Q.push(-sz[x]);
    }

    while(Q.size()){
        int cur = Q.top();
        while(!Q.empty() && Q.top() == cur){
            Q.pop();
            now.push(-cur);
        }
        while((int)now.size() > 2){
            int cur = now.top();
            now.pop();
            now.pop();
            Q.push(-cur * 2);
        }
        while(!now.empty()){
            its.pb(now.top());
            now.pop();
        }
    }
    if(sz[v] < TR3){
        tot = sz[v];
        ans += brute2();
        return;
    }
    if(sz[v] < TR2){
        tot = sz[v];
        ans += brute();
        return;
    }
    // its sorted
    dp.reset();
    dp[0] = true;
    for(int x : its){
        dp |= (dp << x);
    }
    ll mx = 0;
    for(int i=0;i<=sz[v]-1;i++){
        if(dp[i]) mx = max(mx, 1LL * i * (sz[v] - 1 - i));
    }
    ans += mx;
}

void gogo(){
    vector<int> ord;
    ord.pb(1);
    int p = 0;
    while(p < (int)ord.size()){
        int cur = ord[p++];
        for(int x : childs[cur]) ord.pb(x);
    }
    reverse(ord.begin(), ord.end());
    for(int x : ord){
        ev(x);
    }
}

void solve(){
   cin >> n;

    for(int i=2;i<=n;i++){
        int x; cin >> x;
        childs[x].pb(i);
        //adj[x].pb(i);
        //adj[i].pb(x);
    }
    gogo();
    cout << ans << "\n";
}

int main(){
    ios_base::sync_with_stdio(0); cin.tie(0);

    int tt = 1;
    // cin >> tt;
    while(tt--) solve();

    return 0;
}
