#include<bits/stdc++.h>
using namespace std;
const int N=100100;
int a[N],n,d,b;
int gsm(long long l,long long r){
	if(l<1)l=1;if(r>n)r=n;
	return a[r]-a[l-1];
}
int main(){
	scanf("%d%d%d",&n,&d,&b);
	for(int i=1;i<=n;i++)scanf("%d",a+i);
	for(int i=1;i<=n;i++)a[i]+=a[i-1];
	int Lu=0,Ru=0,fcnt1=0,fcnt2=0;
	for(int i=1;i<=n/2;i++){
		int u=gsm(1,i+(long long)i*d);
		if(u>=Lu+b){
			Lu+=b;
		}else fcnt1++;
		u=gsm(n-i+1-(long long)i*d,n);
		if(u>=Ru+b){
			Ru+=b;
		}else fcnt2++;
	}
	printf("%d\n",max(fcnt1,fcnt2));
}
