#include <bits/stdc++.h>
using namespace std;
const int maxn = 2010;
int n, a[2 * maxn], s[2 * maxn], I[maxn][110][110], Z[maxn][110][110];
bool II[maxn][110][110], ZZ[maxn][110][110];
int solveI(int p, int d, int k);
int solveZ(int p, int d, int k);

int solveZ(int p, int d, int k) {
    if (ZZ[p][d][k]) return Z[p][d][k];
    ZZ[p][d][k] = 1;
    Z[p][d][k] = 2e9 + 10;
    int l, r;
    r = n - p;
    l = (p + d) + 1;
    if (r - l + 1 < k) return Z[p][d][k] = 0;
    for (int i = k; i < k + 2 && i <= r - l + 1; i++) {
        /// chon K thang
        int ns = s[r] - s[r - i];
        Z[p][d][k] = min(Z[p][d][k], solveI(p + d, i - d, i) - ns);
    }
    return Z[p][d][k];
}
int solveI(int p, int d, int k) {
    if (II[p][d][k]) return I[p][d][k];
    II[p][d][k] = 1;
    I[p][d][k] = - 2e9 - 10;
    int l, r;
    l = p + 1;
    r = n - (p + d);
    if (r - l + 1 < k) return I[p][d][k] = 0;
    for (int i = k; i < k + 2 && i <= r - l + 1; i++) {
        int ns = s[l + i - 1] - s[l - 1];
        I[p][d][k] = max(I[p][d][k], solveZ(p + d, i - d, i) + ns);
    }
    return I[p][d][k];
}
int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) cin >> a[i];
    for (int i = 1; i <= n; i++) s[i] = s[i - 1] + a[i];
    cout << solveI(0, 0, 1);
}
