#include <bits/stdc++.h>
#define mod 1000000007
#define int long long
using namespace std;
int C[1005][1005],dp[70][1005][2],n,z;
inline int cal(int x)
{
	memset(dp,0,sizeof dp);
	dp[0][0][1]=1;
	for(int i=1;i<=61;i++)
	{
		int X=(x>>i-1)&1;
		for(int j=0;j<=1000;j++)
		{
			if(!dp[i-1][j][0]&&!dp[i-1][j][1]) continue;
			for(int k=(z>>i-1)%2;k<=1000;k+=2)
			{
				int nw=j+k;
				if(nw&1)
				{
					if(X==0)
						dp[i][nw/2][0]=((dp[i-1][j][0]+dp[i-1][j][1])*C[n][k]+dp[i][nw/2][0])%mod;
					else
					{
						dp[i][nw/2][1]=(C[n][k]*dp[i-1][j][1]+dp[i][nw/2][1])%mod;
						dp[i][nw/2][0]=(C[n][k]*dp[i-1][j][0]+dp[i][nw/2][0])%mod;
					}
				}
				else
				{
					if(X==1)
						dp[i][nw/2][1]=((dp[i-1][j][0]+dp[i-1][j][1])*C[n][k]+dp[i][nw/2][1])%mod;
					else
					{
						dp[i][nw/2][1]=(C[n][k]*dp[i-1][j][1]+dp[i][nw/2][1])%mod;
						dp[i][nw/2][0]=(C[n][k]*dp[i-1][j][0]+dp[i][nw/2][0])%mod;
					}
				}
			}
		}
	}
	return dp[61][0][1];
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	for(int i=0;i<=1000;i++)
	{
		C[i][0]=C[i][i]=1;
		for(int j=0;j<i;j++)
			C[i][j]=(C[i-1][j]+C[i-1][j-1])%mod;
	}
	int l,r;
	cin >> n >> l >> r >> z;
	cout << ((cal(r)-cal(l-1)+mod)%mod+mod)%mod ;
	return 0;
}
