#include <bits/stdc++.h>
using namespace std;
#define fo(i,a,b) for(int i=(a);i<(b);i++)
#define MOD 1000000007
#define MP make_pair
#define PB push_back
typedef long long ll;

int T, N, A, w[100100], G, CNT;
ll ans;
string s;

int main () {
	scanf("%d %d", &N, &A);
	map<int,int> m;
	cin >> s;
	fo(i, 0, N) w[i] = s[i] - 'a';
	fo(i, 0, N) {
		scanf("%d", &w[i]);
		w[i] = (m.count(w[i]) ? m[w[i]] : m[w[i]] = ++CNT);
	}
	ll add = 0;
	fo(i, 0, N) if (!i || w[i-1] != w[i]) {
		G++;
		add += -N+1;
	}
	ans = 1ll * G * N * A;

	ans = (ans + add);
	fo(i, 1, N) {
		int a = w[i-1], b = w[i];
		if (a == b) continue;
		if (i-2 < 0 || w[i-2] != b) {
			int j = i+1;
			while (j<N) {
				int n = (j-i)%2 ? a : b;
				if (w[j] == n) j++;
				else break;
			}
			ll x = 1ll * (j-i+1) * (j-i+2) / 2 - 1;
			ans -= x;
		}
	}
	printf("%lld\n", ans-1);
	return 0;
}
