#include <bits/stdc++.h>
#define int long long 
#define pb push_back
using namespace std;
const int INF=1e6+5;
const int Mod=998244353;
int n,a[INF],tot,kk,vis[INF];
vector <int> v3[INF],e[INF];
struct P3{
	int l,r,id;
}aa[INF];
int ksm(int x,int y) {
	int ba=x%Mod,ans=1;
	while (y) {
		if (y&1) ans=(ans*ba)%Mod;
		ba=(ba*ba)%Mod;y>>=1;
	}
	return ans;
}
void DFS(int x) {
	if (vis[x]) return ;
	vis[x]=1;
	for (int v:e[x]) {
		DFS(v);
	}
}
void solve(){
	cin>>n;
	for (int i=1;i<=n;i++) cin>>a[i];
	for (int i=1;i<=n;i++) v3[a[i]].pb(i);
	int kk2=0;
	for (int i=1;i<=2e5;i++) if (v3[i].size()) kk2++;
	for (int i=1;i<=2e5;i++) {
		int len=v3[i].size();
		if (len&1) {cout<<ksm(2,kk2-1)<<"\n";return ;}
		for (int j=0;j<len;j+=2) 
			aa[++tot]={v3[i][j],v3[i][j+1],i};
	}

	sort(aa+1,aa+1+tot,[](P3 xx,P3 yy){return xx.l!=yy.l?xx.l<yy.l:xx.r>yy.r;});
	int res=0,R=aa[1].r;
	for (int i=2;i<=tot;i++) {
		if (aa[i].r<=R) {cout<<ksm(2,kk2-1)<<"\n";return ;}
		else R=aa[i].r;
	}

	for (int i=2;i<=tot;i++) {
		if (aa[i].l<=aa[i-1].r) {
			e[aa[i].id].pb(aa[i-1].id);
			e[aa[i-1].id].pb(aa[i].id);
		}
	}
	for (int i=1;i<=2e5;i++) {
		if (v3[i].size()) {
			if (vis[i]) continue;
			kk++;
			DFS(i);
		}
	}
	cout<<((ksm(2,kk2-1)-ksm(2,kk-1))%Mod+Mod)%Mod<<"\n";
}
signed main()
{
	ios::sync_with_stdio(false);
	int t=1;
	while (t--) solve();
	return 0;
}
